$.fn.ui_sidebar_slide = function (i) {
    var n = {
        obj: $(this),
        viewType: "slidedown",
        addClassParent: !1,
        displayMenu: null,
        displaySubMenu: null
    };
    i && $.extend(n, i);
    var a = null == n.displayMenu ? !1 : !0;
    n.obj.find(".box").on("click", function () {
        var i = $(this)
            , e = i.find(".accordion > a:eq(0)").outerHeight()
            , d = i.find(".accordion > a").length;
        1 == n.addClassParent && i.addClass("active"),
            0 != i.find(".accordion").length && i.find(".group").addClass("active"),
            "default" == n.viewType || a ? i.find(".accordion").css({
                height: e * d
            }).parent().siblings(".box").find(".accordion").css({
                height: 0
            }) : "slidedown" == n.viewType && 0 == a && (0 != i.find(".accordion").length ? i.find(".accordion").animate({
                height: e * d
            }, 200).parent().siblings(".box").find(".accordion").animate({
                height: 0
            }, 200) : 0 == i.find(".accordion").length && n.obj.find(".accordion").animate({
                height: 0
            }, 200)),
            null != n.displaySubMenu && i.find(".accordion").children("a:eq(" + n.displaySubMenu + ")").addClass("active"),
            i.siblings().find(".group").removeClass("active").parent().removeClass("active"),
            a = !1
    }),
        null != n.displayMenu && n.obj.find(".box:eq(" + n.displayMenu + ")").trigger("click")
}
    ;
